hudson.tasks.Shell.command:
#!/bin/bash
if [ ! -z $DEVELOPMENT ]; then exit 0 fi
eval ${SETUPKEYS}
STALE_UNITS=$(eval "${SSHCMD} 'fleetctl list-unit-files|tr -s [:space:]'|grep '@.*\.service'|grep 'launched.inactive.-'|cut -s -f1") if [ "$STALE_UNITS" == "" ]; then echo "nothing to clean" exit 0 fi
echo "---- DEAD UNITS ----" echo $STALE_UNITS echo "---- DESTROYING ----" for unit in $STALE_UNITS; do eval "${SSHCMD} 'fleetctl destroy $unit'" done
