hudson.tasks.Shell.command:
#!/bin/bash
eval ${SETUPKEYS}
AWK_PRE='{n=split($0,a,","); for(i=1; i<=n; i++){ where = match(a[i], /\"' AWK_POST='\"/); if(where) {print a[i]} }}'
for key in $DIRS; do kvpairs=$(curl -sS ${OLDKV}/v1/keys${key} | sed -e 's/^\[//g' | sed -e 's/\]$//g' |sed -e 's/},/} /g') for kv in $kvpairs; do key=$(echo $kv|sed -e 's/[{}]/''/g'|awk -v k="text" "${AWK_PRE}key${AWK_POST}"|cut -d: -f2-) value=$(echo $kv|sed -e 's/[{}]/''/g'|awk -v k="text" "${AWK_PRE}value${AWK_POST}"|cut -d: -f2-) echo "etcdctl set ${key} ${value}" if [ $DRY_RUN == "true" ]; then eval "${SSHCMD} 'etcdctl set ${key} ${value}'" fi done done
