hudson.tasks.Shell.command:
#!/bin/bash eval ${SETUPKEYS}
# set etcd values for cluster to reference echo "---- setting scripts version in etcd ----" eval "${SSHCMD} 'etcdctl set /environment/SCRIPTS-FORK $FORK'" eval "${SSHCMD} 'etcdctl set /environment/SCRIPTS-SHA $SHA'"
# update scripts before submitting again echo "---- updating scripts ----" eval "${SSHCMD} 'fleetctl destroy update-scripts || :'" while [ "$(eval "${SSHCMD} 'fleetctl list-units|grep update-scripts'")" != "" ]; do sleep 1; done eval "${SSHCMD} 'fleetctl submit ${HOST_SCRIPT_DIR}/util-units/update-scripts.service'" eval "${SSHCMD} 'fleetctl start update-scripts'" echo "--------> submitted script update request to cluster..." while [ "$(eval "${SSHCMD} 'fleetctl list-units|grep update-scripts|tr -s [:space:]|cut -s -f3|uniq'")" != "inactive" ] do sleep 1 done echo "--------> cleaning up" eval "${SSHCMD} 'fleetctl destroy update-scripts || :'" while [ "$(eval "${SSHCMD} 'fleetctl list-units|grep update-scripts'")" != "" ]; do sleep 1; done echo "cluster synced"
